CREATE PROCEDURE [dbo].[dsi_sp_BuildDriverTables_EAERMATRIX]
    @SystemID char(12)
AS
SET NOCOUNT ON;
/**********************************************************************************
Client Name: AEROFLOW INC.

Created By: Marie Waters
Business Analyst:  Kim Ephraim
Create Date: 07/28/2023
Service Request Number: SR-2023-00409841

Purpose: Matrix Reliance FMLA Export

Revision History
----------------
Update By           Date           Request Num        Desc
XXXX                XX/XX/2023     SR-2023-000XXXXX   XXXXX

SELECT * FROM dbo.U_dsi_Configuration WHERE FormatCode = 'EAERMATRIX';
SELECT * FROM dbo.U_dsi_SqlClauses WHERE FormatCode = 'EAERMATRIX';
SELECT * FROM dbo.U_dsi_Parameters WHERE FormatCode = 'EAERMATRIX';
SELECT ExpFormatCode, ExpExportCode, ExpStartPerControl, ExpEndPerControl,* FROM dbo.AscExp WHERE expFormatCode = 'EAERMATRIX';
SELECT * FROM dbo.U_dsi_InterfaceActivityLog WHERE FormatCode = 'EAERMATRIX' ORDER BY RunID DESC;

Execute Export
--------------
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EAERMATRIX', 'ONDEM_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EAERMATRIX', 'TEST_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EAERMATRIX', 'OEPASSIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EAERMATRIX', 'OEACTIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EAERMATRIX', 'SCH_EAERMA';

EXEC dbo.dsi_BDM_sp_ErrorCheck 'EAERMATRIX';

EXEC dbo._dsi_usp_ExportRipOut_v7_4 @FormatCode = 'EAERMATRIX', @AllObjects = 'Y', @IsWeb = 'Y'
**********************************************************************************/
BEGIN

    --==========================================
    -- Declare variables
    --==========================================
    DECLARE  @FormatCode        VARCHAR(10)
            ,@ExportCode        VARCHAR(10)
            ,@StartDate         DATETIME
            ,@EndDate           DATETIME
            ,@StartPerControl   VARCHAR(9)
            ,@EndPerControl     VARCHAR(9)
            ,@FileMinCovDate    DATETIME;

    -- Set FormatCode
    SELECT @FormatCode = 'EAERMATRIX';

    -- Declare dates from Parameter file
    SELECT
         @StartPerControl = StartPerControl
        ,@EndPerControl   = EndPerControl
        ,@StartDate       = LEFT(StartPerControl,8)
        ,@EndDate         = DATEADD(S,-1,DATEADD(D,1,LEFT(EndPerControl,8)))
        ,@ExportCode      = ExportCode
        ,@FileMinCovDate  = '06/01/2023'
    FROM dbo.U_dsi_Parameters WITH (NOLOCK)
    WHERE FormatCode = @FormatCode;

    --==========================================
    -- Clean EE List 
    -- Caution: Careful of cleaning EE List if including paycheck data
    --==========================================

    -- Cleans EE List of terms where EE active in another company (transfer), or active in more than one company
    DELETE FROM dbo.U_EAERMATRIX_EEList
    WHERE xCoID <> dbo.dsi_BDM_fn_GetCurrentCOID(xEEID)
    AND xEEID IN (SELECT xEEID FROM dbo.U_EAERMATRIX_EEList GROUP BY xEEID HAVING COUNT(1) > 1);

    --==========================================
    -- Create Deduction List
    --==========================================
    DECLARE @DedList VARCHAR(MAX)
    SET @DedList = 'DED1,DED2';

    IF OBJECT_ID('U_EAERMATRIX_DedList','U') IS NOT NULL
        DROP TABLE dbo.U_EAERMATRIX_DedList;
    SELECT DISTINCT
         DedCode = DedDedCode
        ,DedType = DedDedType
    INTO dbo.U_EAERMATRIX_DedList
    FROM dbo.fn_ListToTable(@DedList)
    JOIN dbo.DedCode WITH (NOLOCK)
        ON DedDedCode = Item;


    --==========================================
    -- BDM Section
    --==========================================
    DELETE FROM dbo.U_dsi_BDM_Configuration WHERE FormatCode = @FormatCode;

    -- Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'DedCodes','MED,DEN,VIS');
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'StartDateTime',@StartDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'EndDateTime',@EndDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'TermSelectionOption','AuditDate');

    -- Non-Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'BuildConsolidatedTable','Standard');

    -- Required OE parameters
    IF @ExportCode LIKE '%PASSIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','PASSIVE');
    END;

    IF @ExportCode LIKE '%ACTIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','ACTIVE');
    END;


    -- Run BDM Module
    EXEC dbo.dsi_BDM_sp_PopulateDeductionsTable @FormatCode;

    --==========================================
    -- Build Working Tables
    --==========================================

    -----------------------------
    -- Working Table - PDedHist
    -----------------------------
    IF OBJECT_ID('U_EAERMATRIX_PDedHist','U') IS NOT NULL
        DROP TABLE dbo.U_EAERMATRIX_PDedHist;
    SELECT DISTINCT
         PdhEEID
        -- Current Payroll Amounts
        ,PdhEECurAmt    = SUM(CASE WHEN PdhPerControl BETWEEN @StartPerControl AND @EndPerControl THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhERCurAmt    = SUM(CASE WHEN PdhPerControl BETWEEN @StartPerControl AND @EndPerControl THEN PdhERCurAmt ELSE 0.00 END)
        -- YTD Payroll Amounts
        ,PdhEECurAmtYTD = SUM(PdhEECurAmt)
        ,PdhERCurAmtYTD = SUM(PdhERCurAmt)
        -- Categorize Payroll Amounts
        ,PdhSource1     = SUM(CASE WHEN PdhDedCode IN ('401K') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource2     = SUM(CASE WHEN PdhDedCode IN ('ROTH') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource3     = SUM(CASE WHEN PdhDedCode IN ('MATCH') THEN PdhERCurAmt ELSE 0.00 END)        
        ,PdhSource4     = SUM(CASE WHEN PdhDedCode IN ('401CU') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource5     = SUM(CASE WHEN PdhDedCode IN ('ROTHC') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource6     = SUM(CASE WHEN PdhDedCode IN ('401KL1') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource7     = SUM(CASE WHEN PdhDedCode IN ('401KL2') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource8     = SUM(CASE WHEN PdhDedCode IN ('401KL3') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource9     = SUM(CASE WHEN PdhDedCode IN ('401KL4') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource10    = SUM(CASE WHEN PdhDedCode IN ('401KL5') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
    INTO dbo.U_EAERMATRIX_PDedHist
    FROM dbo.PDedHist WITH (NOLOCK)
    JOIN dbo.U_EAERMATRIX_DedList WITH (NOLOCK)
        ON DedCode = PdhDedCode
    WHERE LEFT(PdhPerControl,4) = LEFT(@EndPerControl,4)
    AND PdhPerControl <= @EndPerControl
    AND PdhPerControl BETWEEN @StartPerControl AND @EndPerControl -- Filter for Current Payroll Dates. If you need YTD Totals, then remove or comment out this line.
    GROUP BY PdhEEID
    HAVING (SUM(PdhEECurAmt) <> 0.00
        OR SUM(PdhERCurAmt) <> 0.00
    );


    -----------------------------
    -- Working Table - PEarHist
    -----------------------------
    IF OBJECT_ID('U_EAERMATRIX_PEarHist','U') IS NOT NULL
        DROP TABLE dbo.U_EAERMATRIX_PEarHist;
    SELECT DISTINCT
         PehEEID
        ,PrgPayDate             = MAX(PrgPayDate)
        -- Current Payroll Amount/Hours
        ,PehCurAmt              = SUM(CASE WHEN PehPerControl >= @StartPerControl THEN PehCurAmt ELSE 0.00 END)
        ,PehCurHrs              = SUM(CASE WHEN PehPerControl >= @StartPerControl THEN PehCurHrs ELSE 0.00 END)
        -- YTD Payroll Amount/Hours
        ,PehCurAmtYTD           = SUM(PehCurAmt)
        ,PehCurHrsYTD           = SUM(PehCurHrs)
        -- Current Include Deferred Comp Amount/Hours
        ,PehInclInDefComp       = SUM(CASE WHEN PehInclInDefComp = 'Y' AND PehPerControl >= @StartPerControl THEN PehCurAmt END)
        ,PehInclInDefCompHrs    = SUM(CASE WHEN PehInclInDefCompHrs = 'Y' AND PehPerControl >= @StartPerControl THEN PehCurHrs END)
        -- YTD Include Deferred Comp Amount/Hours
        ,PehInclInDefCompYTD    = SUM(CASE WHEN PehInclInDefComp = 'Y' THEN PehCurAmt END)
        ,PehInclInDefCompHrsYTD = SUM(CASE WHEN PehInclInDefCompHrs = 'Y' THEN PehCurHrs END)
        ,PehRegularPayHrsYTD = SUM(CASE WHEN ErnIsRegularPayCode = 'Y' THEN PehCurHrs END)
    INTO dbo.U_EAERMATRIX_PEarHist
    FROM dbo.vw_int_PayReg WITH (NOLOCK)
    JOIN dbo.vw_int_PEarHist WITH (NOLOCK)
        ON PehGenNumber = PrgGenNumber
    LEFT JOIN dbo.EarnCode WITH (NOLOCK)
        ON ErnEarnCode = PehEarnCode
    WHERE  PrgPayDate between  CAST(CAST(DATEADD(M,-12,@EndDate) AS date) AS datetime) and @EndDate
    --LEFT(PehPerControl,4) = LEFT(@EndPerControl,4)
 --   AND PehPerControl <= @EndPerControl
    GROUP BY PehEEID
    HAVING SUM(PehCurAmt) <> 0.00;


    -----------------------------
    -- Working Table - PTaxHist
    -----------------------------
    IF OBJECT_ID('U_EMASMUTUAL_PTaxHist') IS NOT NULL
        DROP TABLE dbo.U_EMASMUTUAL_PTaxHist;
    SELECT DISTINCT
         PthEEID
        ,PthTaxableWagesYTD    = SUM(PthCurTaxableWages)       
    INTO dbo.U_EAERMATRIX_PTaxHist
    FROM dbo.U_EAERMATRIX_EEList
    JOIN dbo.PTaxHist WITH (NOLOCK) 
        ON PthEEID = xEEID
        AND PthCoID = xCoID
    WHERE LEFT(PthPerControl,4) = LEFT(@EndPerControl,4)
    AND PthPerControl <= @EndPerControl
    AND PthTaxCode = 'USFIT'
    GROUP BY PthEEID
    HAVING SUM(PthCurGrossWages) <> 0.00;


    --==========================================
    -- Build Driver Tables
    --==========================================
    ---------------------------------
    -- DETAIL RECORD - U_EAERMATRIX_drvTbl
    ---------------------------------
    IF OBJECT_ID('U_EAERMATRIX_drvTbl','U') IS NOT NULL
        DROP TABLE dbo.U_EAERMATRIX_drvTbl;
    SELECT DISTINCT
         drvEEID = xEEID
        ,drvCoID = xCoID
        ,drvDepRecID = CONVERT(varchar(12),'1') --DELETE IF NOT USING DEPENDENT DATA
        ,drvInitialSort = ''
        ,drvSubSort = ''
        -- standard fields above and additional driver fields below
        ,drvDateOfBirth = convert(varchar,EepDateOfBirth,101)
        ,drvDateOfHireMostRecent = convert(varchar,EecDateofLastHire,101)
        ,drvDateofHireOriginal = convert(varchar,EecDateofOriginalHire,101)
        ,drvDateofHireAdjusted = convert(varchar,EecDateOfSeniority,101)
        ,drvSSN = stuff(stuff(eepSSN,6,0,'-'),4,0,'-')
        ,drvEmloyeeID = EecEmpNo
        ,drvFirstName = EepNameFirst
        ,drvMiddleInitial = LEFT(EepNameMiddle,1)
        ,drvLastName = EepNameLast
        ,drvHomeAddress1 = EepAddressLine1
        ,drvHomeAddress2 = EepAddressLine2
        ,drvHomeAddress3 = ''
        ,drvHomeCity = EepAddressCity
        ,drvHomeState = EepAddressState
        ,drvHomeZip = EepAddressZipCode
        ,drvHomePhone = CASE WHEn efoPhoneNumber  is not null then efoPhoneNumber  ELSE EepPhoneHomeNumber END
        ,drvHomeEmailAddress = EepAddressEMailAlternate
        ,drvMaritalStatus = CASE WHEN eepMaritalStatus IN ('S' , 'M', 'D', 'W' ) THEN eepMaritalStatus  END
        ,drvGender = EepGender
        ,drvWorkState = LEFT(EecStateSUI,2)
        ,drvWorkEmailAddress = dbo.dsi_fnlib_GetSupervisorField('', EecEEID, 'AddressEMail')
        ,drvDepartmentNumber = EecOrglvl3
        ,drvDepartmentName = o3.OrgDesc 
        ,drvLocationCode = LocAddressState
        ,drvLocationName = LocDesc
        ,drvPosition =  JbcDesc 
     
        ,drvIsActive = CASE WHEN Eecemplstatus = 'T' THEN 'N' Else 'Y' END
        ,drvTerminationDate = CASE WHEN EecEmplStatus = 'T' THEN EecDateOfTermination END
        ,drvIsFulltime = CASE WHEN EecFullTimeOrPartTime = 'F' THEN 'Y' ELSE 'N' END
        ,drvIsExempt = CASE WHEN EjhFLSACategory = 'E' THEN 'Y' Else 'N' END
        ,drvFICAYearToDate= ''
        ,drvSupervisorName = dbo.dsi_fnlib_GetSupervisorField('', EecEEID, 'NameFirst') + '' + dbo.dsi_fnlib_GetSupervisorField('', EecEEID, 'NameLast') 
        ,drvSupervisorEmailAddress = CASE WHEN dbo.dsi_fnlib_IsSupervisor(xEEID) = 'Y' THEN EepAddressEMail END
        ,drvSupervisorPhone = CASE WHEN dbo.dsi_fnlib_IsSupervisor(xEEID) = 'Y' THEN EecPhoneBusinessNumber END
        ,drvSubGroupA = LocAddressState
        ,drvSubGroupB = CmpCompanyName
        ,drvSubGroupC = o1.OrgDesc 
        ,drvSubGroupD = o2.OrgDesc 
        ,drvIsSTDEligible = Case when BdmDedCode IN ('STD1','STD2','VSTD1','VSTD2') AND BdmBenOption = 'A' THEN 'Y' ELSE 'N' END
        ,drvStdPlanEffective = Case when BdmDedCode  IN ('STD1','STD2','VSTD1','VSTD2') AND BdmdBenOption = 'A' THEN 'Y'ELSE 'N' END
        ,drvIsLTDEligible = ''
        ,drvLTDEffectiveDate = ''
        ,drvHoursWorked = PehRegularPayHrsYTD
        ,drvAdd08WIPaidHrsWorked = Case when BdmDedCode IN ('STD1','STD2','VSTD1','VSTD2') AND EecStateSUI = 'WI' THEN PehRegularPayHrsYTD END
        ,drvAddData09MAPFLearnings = CASE WHEN BdmDedCode IN ('STD1','STD2','VSTD1','VSTD2') AND EepcStateSUI = 'MA' THEN '' END
        ,drvIsEmployeeVCIEligible = Case WHEN BdmDedCode IN ('ACRT1','ACRT2','CLF2','CRT21','CRT22','CRT31','CRT32') AND BdmBenStatus IN ('A','T') THEN 'Y' Else 'N' END
        ,drvVCIStartDate = Case when BdmDedCode IN ('ACRT1','ACRT2','CLF2','CRT21','CRT22','CRT31','CRT32') AND BdmBenStatus IN ('A','T') THEN dbo.dsi_fnGetMinMaxDates('MAX',BdmBenStartDate, @FileMinCovDate) END 
        ,drvVCIEndDate = Case when BdmDedCode IN ('ACRT1','ACRT2','CLF2','CRT21','CRT22','CRT31','CRT32') AND BdmBenStatus IN ('A','T') THEN dbo.dsi_fnGetMinMaxDates('MAX',BdmBenStartDate, @FileMinCovDate) END
        ,drvVCIPremiumDeductionAmount = CASE when EedDedCode IN ('ACRT1','ACRT2','CLF2','CRT21','CRT22','CRT31','CRT32')  THEN PehCurAmt END
    INTO dbo.U_EAERMATRIX_drvTbl
    FROM dbo.U_EAERMATRIX_EEList WITH (NOLOCK)
    JOIN dbo.vw_int_EmpComp WITH (NOLOCK)
        ON EecEEID = xEEID 
        AND EecCoID = xCoID
    JOIN dbo.EmpPers WITH (NOLOCK)
        ON EepEEID = xEEID
    JOIN dbo.U_dsi_BDM_EAERMATRIX WITH (NOLOCK)
        ON BdmEEID = xEEID 
        AND BdmCoID = xCoID
     JOIN dbo.Company WITH (NOLOCK)
        ON CmpCoID = xCoID
    LEFT JOIN dbo.EmpMPhon WITH (NOLOCK)
        ON EfoEEID = xEEID
        AND EfoPhoneType = 'CEL'
         LEFT JOIN dbo.OrgLevel O1 WITH (NOLOCK)
        ON O1.OrgCode = EecOrglvl1
        AND O1.OrgLvl = '1'
         LEFT JOIN dbo.OrgLevel O2 WITH (NOLOCK)
        ON O2.OrgCode = EecOrglvl2
        AND O2.OrgLvl = '2'
     LEFT JOIN dbo.OrgLevel O3 WITH (NOLOCK)
        ON O3.OrgCode = EecOrglvl3
        AND O3.OrgLvl = '3'
    LEFT JOIN dbo.Location WITH (NOLOCK)
        On LocCode = EecLocation
      JOIN dbo.JobCode WITH (NOLOCK)
        ON JbcJobCode = EecJobCode
    LEFT JOIN (
            SELECT EjhEEID, EjhCOID, EjhJObCode, EjhFLSACategory, EjhJobEffDate, EjhWeeklyHours
            FROM (
                    SELECT EjhEEID, EjhCOID, EjhJObCode, EjhFLSACategory, EjhJobEffDate, EjhWeeklyHours, ROW_NUMBER() OVER(PARTITION BY EjhEEID, EjhCOID ORDER BY EjhJobEffDate DESC) AS RN
                    FROM dbo.EmpHJob WITH (NOLOCK)) AS X
            WHERE RN = 1) AS EJH
        ON EjhEEID = xEEID
        AND EjhCOID = xCOID
        AND EjhJobCode = EecJobCode 
    ;
    ---------------------------------
    -- HEADER RECORD
    ---------------------------------
    IF OBJECT_ID('U_EAERMATRIX_Header','U') IS NOT NULL
        DROP TABLE dbo.U_EAERMATRIX_Header;
    SELECT DISTINCT
         drvIsSTDSuppBuyUpPlanEligible = 'Is STD Supp/Buy Up  Plan Eligible'
        ,drvSTDSuppBuyUpPlanEffDate = 'STD Supp/Buy Up  Plan  Effective Date'
        ,drvIsCaliVoluntaryPlanElig = 'Is California Voluntary Plan Eligible'
        ,drvCaliVoluntaryPlanEffective = 'California Voluntary Plan Effective'
        ,drvIsLTDSuppBuyUpPlanElig = 'Is LTD Supp/Buy Up Plan Eligible'
        ,drvLTDSuppBuyUpPlanEffDate = 'LTD Supp/Buy Up Plan Effective Date'
        ,drvSpouseWorksatSameCompany = 'Spouse Working at the Same Company'
        ,drvEmployee50in75MileInd = 'Employees 50 in 75 Mile Indicator'
        ,drvAddData08WIPaidHrsWorked = 'Add Data 08 - WI Paid Hours Worked'
    INTO dbo.U_EAERMATRIX_Header
    ;

    --==========================================
    -- Set FileName
    --==========================================
    IF (dbo.dsi_fnVariable(@FormatCode,'UseFileName') = 'N')
    BEGIN
        UPDATE dbo.U_dsi_Parameters
            SET ExportFile = CASE WHEN dbo.dsi_fnVariable(@FormatCode,'Testing') = 'Y' THEN 'Test_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  WHEN @ExportCode LIKE 'OE%' THEN 'OE_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  ELSE 'Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                             END
        WHERE FormatCode = @FormatCode;
    END

END;
