CREATE PROCEDURE [dbo].[dsi_sp_BuildDriverTables_EHENNYLEXP]
    @SystemID char(12)
AS
SET NOCOUNT ON;
/**********************************************************************************
Client Name: Henderson Engineers

Created By: Roger Bynum
Business Analyst: Katherine Ricca
Create Date: 11/08/2022
Service Request Number:  TekP-2022-09-30-01

Purpose: NY Life LTD/STD Export

Revision History
----------------
Update By           Date           Request Num        Desc
XXXX                XX/XX/2022     SR-2022-000XXXXX   XXXXX

SELECT * FROM dbo.U_dsi_Configuration WHERE FormatCode = 'EHENNYLEXP';
SELECT * FROM dbo.U_dsi_SqlClauses WHERE FormatCode = 'EHENNYLEXP';
SELECT * FROM dbo.U_dsi_Parameters WHERE FormatCode = 'EHENNYLEXP';
SELECT ExpFormatCode, ExpExportCode, ExpStartPerControl, ExpEndPerControl,* FROM dbo.AscExp WHERE expFormatCode = 'EHENNYLEXP';
SELECT * FROM dbo.U_dsi_InterfaceActivityLog WHERE FormatCode = 'EHENNYLEXP' ORDER BY RunID DESC;

Execute Export
--------------
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EHENNYLEXP', 'ONDEM_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EHENNYLEXP', 'TEST_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EHENNYLEXP', 'OEPASSIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EHENNYLEXP', 'OEACTIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EHENNYLEXP', 'SCH_EHENNY';

EXEC dbo.dsi_BDM_sp_ErrorCheck 'EHENNYLEXP';

EXEC dbo._dsi_usp_ExportRipOut_v7_4 @FormatCode = 'EHENNYLEXP', @AllObjects = 'Y', @IsWeb = 'Y'
**********************************************************************************/
BEGIN

    --==========================================
    -- Declare variables
    --==========================================
    DECLARE  @FormatCode        VARCHAR(10)
            ,@ExportCode        VARCHAR(10)
            ,@StartDate         DATETIME
            ,@EndDate           DATETIME
            ,@StartPerControl   VARCHAR(9)
            ,@EndPerControl     VARCHAR(9);

    -- Set FormatCode
    SELECT @FormatCode = 'EHENNYLEXP';

    -- Declare dates from Parameter file
    SELECT
         @StartPerControl = StartPerControl
        ,@EndPerControl   = EndPerControl
        ,@StartDate       = LEFT(StartPerControl,8)
        ,@EndDate         = DATEADD(S,-1,DATEADD(D,1,LEFT(EndPerControl,8)))
        ,@ExportCode      = ExportCode
    FROM dbo.U_dsi_Parameters WITH (NOLOCK)
    WHERE FormatCode = @FormatCode;

    --==========================================
    -- Clean EE List 
    -- Caution: Careful of cleaning EE List if including paycheck data
    --==========================================

    -- Cleans EE List of terms where EE active in another company (transfer), or active in more than one company
    DELETE FROM dbo.U_EHENNYLEXP_EEList
    WHERE xCoID <> dbo.dsi_BDM_fn_GetCurrentCOID(xEEID)
    AND xEEID IN (SELECT xEEID FROM dbo.U_EHENNYLEXP_EEList GROUP BY xEEID HAVING COUNT(1) > 1);

    --==========================================
    -- Create Deduction List
    --==========================================
    DECLARE @DedList VARCHAR(MAX)
    SET @DedList = 'DED1,DED2';

    IF OBJECT_ID('U_EHENNYLEXP_DedList','U') IS NOT NULL
        DROP TABLE dbo.U_EHENNYLEXP_DedList;
    SELECT DISTINCT
         DedCode = DedDedCode
        ,DedType = DedDedType
    INTO dbo.U_EHENNYLEXP_DedList
    FROM dbo.fn_ListToTable(@DedList)
    JOIN dbo.DedCode WITH (NOLOCK)
        ON DedDedCode = Item;


    --==========================================
    -- BDM Section
    --==========================================
    DELETE FROM dbo.U_dsi_BDM_Configuration WHERE FormatCode = @FormatCode;

    -- Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'DedCodes','STD,LTD');
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'StartDateTime',@StartDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'EndDateTime',@EndDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'TermSelectionOption','AuditDate');

    -- Non-Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'BuildConsolidatedTable','Standard');

    -- Required OE parameters
    IF @ExportCode LIKE '%PASSIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','PASSIVE');
    END;

    IF @ExportCode LIKE '%ACTIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','ACTIVE');
    END;


    -- Run BDM Module
    EXEC dbo.dsi_BDM_sp_PopulateDeductionsTable @FormatCode;

    --==========================================
    -- Build Driver Tables
    --==========================================
    ---------------------------------
    -- DETAIL RECORD - U_EHENNYLEXP_drvTbl
    ---------------------------------
    IF OBJECT_ID('U_EHENNYLEXP_drvTbl','U') IS NOT NULL
        DROP TABLE dbo.U_EHENNYLEXP_drvTbl;
    SELECT DISTINCT
         drvEEID = xEEID
        ,drvCoID = xCoID
        ,drvDepRecID = CONVERT(varchar(12),'1') --DELETE IF NOT USING DEPENDENT DATA
        ,drvSort = ''
        -- standard fields above and additional driver fields below
        ,drvSSN = eepSSN
        ,drvEmployeeIdNumber = EecEmpNo
        ,drvNameLast = EepNameLast
        ,drvNameFirst = EepNameFirst
        ,drvNameMiddle = LEFT(EepNameMiddle,1)
        ,drvNameSuffix = NULLIF(EepNameSuffix,'Z')
        ,drvDateOfBirth = EepDateOfBirth
        ,drvGender = EepGender
        ,drvMaritalStatus = eepMaritalStatus
        ,drvAddressLine1 = EepAddressLine1
        ,drvAddressLine2 = EepAddressLine2
        ,drvAddressCity = EepAddressCity
        ,drvAddressState = EepAddressState
        ,drvAddressZipCode = EepAddressZipCode
,drvEECountry = CASE WHEN EepAddressCountry = 'USA' THEN 'US'
                             WHEN EepAddressCountry = 'CAN' THEN 'Canada'
                             ELSE 'ZZ'
                        END
        ,drvCountryCode = CASE WHEN EepAddressCountry = 'USA' THEN 'US'
                             WHEN EepAddressCountry = 'CAN' THEN 'CA'
                             ELSE 'ZZ'
                        END
        ,drvPhoneNumber = EepPhoneHomeNumber
        ,drvEmploymentStatusCode = CASE EecEmplstatus WHEN 'A' THEN 'AC' WHEN 'L' THEN 'L1' WHEN 'T' THEN 'T' ELSE '' END  --Todo Check others codes
        ,drvEmployeeStatusEffDate = EecEmplStatusStartDate
        ,drvEmployeeType = EecEEType
        ,drvDateOfLastHire = EecDateOfLastHire
        ,drvEarningsForDisabProduct = ''
        ,drvSalariedIndicator = ''
        ,drvFullTimeIndicator = ''
        ,drvNumberofHoursWorkedPerSeek = ''
        ,drvTaxFilingState = EepAddressState
        ,drvWorkState = EepAddressState
        ,drvProvisionEffectiveDate = ''
        ,drvExtendedEEEmailAddress = EepAddressEMail
        ,drvExtendedSupEmailAddress1 = EepAddressLine1
    INTO dbo.U_EHENNYLEXP_drvTbl
    FROM dbo.U_EHENNYLEXP_EEList WITH (NOLOCK)
    JOIN dbo.vw_int_EmpComp WITH (NOLOCK)
        ON EecEEID = xEEID 
        AND EecCoID = xCoID
    JOIN dbo.EmpPers WITH (NOLOCK)
        ON EepEEID = xEEID
    JOIN dbo.U_dsi_BDM_EHENNYLEXP WITH (NOLOCK)
        ON BdmEEID = xEEID 
        AND BdmCoID = xCoID
    ;
    ---------------------------------
    -- HEADER RECORD
    ---------------------------------
    IF OBJECT_ID('U_EHENNYLEXP_Header','U') IS NOT NULL
        DROP TABLE dbo.U_EHENNYLEXP_Header;
    SELECT DISTINCT
         drvFileCreationDate = ''
    INTO dbo.U_EHENNYLEXP_Header
    ;
    ---------------------------------
    -- TRAILER RECORD
    ---------------------------------
    IF OBJECT_ID('U_EHENNYLEXP_Trailer','U') IS NOT NULL
        DROP TABLE dbo.U_EHENNYLEXP_Trailer;
    SELECT DISTINCT
         drvNumberOfRecords = ''
    INTO dbo.U_EHENNYLEXP_Trailer
    ;

    --==========================================
    -- Set FileName
    --==========================================
    IF (dbo.dsi_fnVariable(@FormatCode,'UseFileName') = 'N')
    BEGIN
        UPDATE dbo.U_dsi_Parameters
            SET ExportFile = CASE WHEN dbo.dsi_fnVariable(@FormatCode,'Testing') = 'Y' THEN 'Test_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  WHEN @ExportCode LIKE 'OE%' THEN 'OE_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  ELSE 'Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                             END
