CREATE PROCEDURE [dbo].[dsi_sp_BuildDriverTables_EDOENYLBAX]
    @SystemID char(12)
AS
SET NOCOUNT ON;
/**********************************************************************************
Client Name: The Doe Run Company

Created By: Marco Lagrosa
Business Analyst: Lea King
Create Date: 09/19/2021
Service Request Number: TekP-2021-08-10-0004

Purpose: NY Life Beneficiary Admin

Revision History
----------------
Update By           Date           Request Num        Desc
XXXX                XX/XX/2021     SR-2021-000XXXXX   XXXXX

SELECT * FROM dbo.U_dsi_Configuration WHERE FormatCode = 'EDOENYLBAX';
SELECT * FROM dbo.U_dsi_SqlClauses WHERE FormatCode = 'EDOENYLBAX';
SELECT * FROM dbo.U_dsi_Parameters WHERE FormatCode = 'EDOENYLBAX';
SELECT ExpFormatCode, ExpExportCode, ExpStartPerControl, ExpEndPerControl,* FROM dbo.AscExp WHERE expFormatCode = 'EDOENYLBAX';
SELECT * FROM dbo.U_dsi_InterfaceActivityLog WHERE FormatCode = 'EDOENYLBAX' ORDER BY RunID DESC;

Execute Export
--------------
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EDOENYLBAX', 'ONDEM_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EDOENYLBAX', 'TEST_XOE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EDOENYLBAX', 'OEPASSIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EDOENYLBAX', 'OEACTIVE';
EXEC dbo.dsi_sp_TestSwitchbox_v2 'EDOENYLBAX', 'SCH_EDOENY';

EXEC dbo.dsi_BDM_sp_ErrorCheck 'EDOENYLBAX';

EXEC dbo._dsi_usp_ExportRipOut_v7_4 @FormatCode = 'EDOENYLBAX', @AllObjects = 'Y', @IsWeb = 'Y'
**********************************************************************************/
BEGIN

    --==========================================
    -- Declare variables
    --==========================================
    DECLARE  @FormatCode        VARCHAR(10)
            ,@ExportCode        VARCHAR(10)
            ,@StartDate         DATETIME
            ,@EndDate           DATETIME
            ,@StartPerControl   VARCHAR(9)
            ,@EndPerControl     VARCHAR(9);

    -- Set FormatCode
    SELECT @FormatCode = 'EDOENYLBAX';

    -- Declare dates from Parameter file
    SELECT
         @StartPerControl = StartPerControl
        ,@EndPerControl   = EndPerControl
        ,@StartDate       = LEFT(StartPerControl,8)
        ,@EndDate         = DATEADD(S,-1,DATEADD(D,1,LEFT(EndPerControl,8)))
        ,@ExportCode      = ExportCode
    FROM dbo.U_dsi_Parameters WITH (NOLOCK)
    WHERE FormatCode = @FormatCode;

    --==========================================
    -- Clean EE List 
    -- Caution: Careful of cleaning EE List if including paycheck data
    --==========================================

    -- Cleans EE List of terms where EE active in another company (transfer), or active in more than one company
    DELETE FROM dbo.U_EDOENYLBAX_EEList
    WHERE xCoID <> dbo.dsi_BDM_fn_GetCurrentCOID(xEEID)
    AND xEEID IN (SELECT xEEID FROM dbo.U_EDOENYLBAX_EEList GROUP BY xEEID HAVING COUNT(1) > 1);


    --==========================================
    -- Create Deduction List
    --==========================================
    DECLARE @DedList VARCHAR(MAX)
    SET @DedList = 'GLIFE,AADD ,ALFE2,ALFE3,ALFE4,ALFE5,ALFE6,ADDE1,EADD1,EADD2,EADD3,EADD4,EADD5';

    IF OBJECT_ID('U_EDOENYLBAX_DedList','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_DedList;
    SELECT DISTINCT
         DedCode = DedDedCode
        ,DedType = DedDedType
    INTO dbo.U_EDOENYLBAX_DedList
    FROM dbo.fn_ListToTable(@DedList)
    JOIN dbo.DedCode WITH (NOLOCK)
        ON DedDedCode = Item;


    --==========================================
    -- BDM Section
    --==========================================
    DELETE FROM dbo.U_dsi_BDM_Configuration WHERE FormatCode = @FormatCode;

    -- Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'DedCodes',@DedList);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'StartDateTime',@StartDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'EndDateTime',@EndDate);
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES(@FormatCode,'TermSelectionOption','AuditDate');

    -- Non-Required parameters
    INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'BuildConsolidatedTable','Standard');

    -- Required OE parameters
    IF @ExportCode LIKE '%PASSIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','PASSIVE');
    END;

    IF @ExportCode LIKE '%ACTIVE'
    BEGIN
        INSERT INTO dbo.U_dsi_BDM_Configuration VALUES (@FormatCode,'OEType','ACTIVE');
    END;


    -- Run BDM Module
    EXEC dbo.dsi_BDM_sp_PopulateDeductionsTable @FormatCode;

    EXEC dbo.dsi_bdm_sp_CalculateBenefitAmounts @FormatCode;

    --==========================================
    -- Build Working Tables
    --==========================================

    -----------------------------
    -- Working Table - PDedHist
    -----------------------------
    IF OBJECT_ID('U_EDOENYLBAX_PDedHist','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_PDedHist;
    SELECT DISTINCT
         PdhEEID
        -- Current Payroll Amounts
        ,PdhEECurAmt    = SUM(CASE WHEN PdhPerControl BETWEEN @StartPerControl AND @EndPerControl THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhERCurAmt    = SUM(CASE WHEN PdhPerControl BETWEEN @StartPerControl AND @EndPerControl THEN PdhERCurAmt ELSE 0.00 END)
        -- YTD Payroll Amounts
        ,PdhEECurAmtYTD = SUM(PdhEECurAmt)
        ,PdhERCurAmtYTD = SUM(PdhERCurAmt)
        -- Categorize Payroll Amounts
        ,PdhSource1     = SUM(CASE WHEN PdhDedCode IN ('401K') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource2     = SUM(CASE WHEN PdhDedCode IN ('ROTH') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource3     = SUM(CASE WHEN PdhDedCode IN ('MATCH') THEN PdhERCurAmt ELSE 0.00 END)        
        ,PdhSource4     = SUM(CASE WHEN PdhDedCode IN ('401CU') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource5     = SUM(CASE WHEN PdhDedCode IN ('ROTHC') THEN PdhEECurAmt ELSE 0.00 END)
        ,PdhSource6     = SUM(CASE WHEN PdhDedCode IN ('401KL1') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource7     = SUM(CASE WHEN PdhDedCode IN ('401KL2') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource8     = SUM(CASE WHEN PdhDedCode IN ('401KL3') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource9     = SUM(CASE WHEN PdhDedCode IN ('401KL4') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
        ,PdhSource10    = SUM(CASE WHEN PdhDedCode IN ('401KL5') THEN ISNULL(PdhEECurAmt, 0) ELSE 0.00 END)
    INTO dbo.U_EDOENYLBAX_PDedHist
    FROM dbo.PDedHist WITH (NOLOCK)
    JOIN dbo.U_EDOENYLBAX_DedList WITH (NOLOCK)
        ON DedCode = PdhDedCode
    WHERE LEFT(PdhPerControl,4) = LEFT(@EndPerControl,4)
    AND PdhPerControl <= @EndPerControl
    AND PdhPerControl BETWEEN @StartPerControl AND @EndPerControl -- Filter for Current Payroll Dates. If you need YTD Totals, then remove or comment out this line.
    GROUP BY PdhEEID
    HAVING (SUM(PdhEECurAmt) <> 0.00
        OR SUM(PdhERCurAmt) <> 0.00
    );


    -----------------------------
    -- Working Table - PEarHist
    -----------------------------
    IF OBJECT_ID('U_EDOENYLBAX_PEarHist','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_PEarHist;
    SELECT DISTINCT
         PehEEID
        ,PrgPayDate             = MAX(PrgPayDate)
        -- Current Payroll Amount/Hours
        ,PehCurAmt              = SUM(CASE WHEN PehPerControl >= @StartPerControl THEN PehCurAmt ELSE 0.00 END)
        ,PehCurHrs              = SUM(CASE WHEN PehPerControl >= @StartPerControl THEN PehCurHrs ELSE 0.00 END)
        -- YTD Payroll Amount/Hours
        ,PehCurAmtYTD           = SUM(PehCurAmt)
        ,PehCurHrsYTD           = SUM(PehCurHrs)
        -- Current Include Deferred Comp Amount/Hours
        ,PehInclInDefComp       = SUM(CASE WHEN PehInclInDefComp = 'Y' AND PehPerControl >= @StartPerControl THEN PehCurAmt END)
        ,PehInclInDefCompHrs    = SUM(CASE WHEN PehInclInDefCompHrs = 'Y' AND PehPerControl >= @StartPerControl THEN PehCurHrs END)
        -- YTD Include Deferred Comp Amount/Hours
        ,PehInclInDefCompYTD    = SUM(CASE WHEN PehInclInDefComp = 'Y' THEN PehCurAmt END)
        ,PehInclInDefCompHrsYTD = SUM(CASE WHEN PehInclInDefCompHrs = 'Y' THEN PehCurHrs END)
    INTO dbo.U_EDOENYLBAX_PEarHist
    FROM dbo.vw_int_PayReg WITH (NOLOCK)
    JOIN dbo.vw_int_PEarHist WITH (NOLOCK)
        ON PehGenNumber = PrgGenNumber
    WHERE LEFT(PehPerControl,4) = LEFT(@EndPerControl,4)
    AND PehPerControl <= @EndPerControl
    GROUP BY PehEEID
    HAVING SUM(PehCurAmt) <> 0.00;
    --==========================================
    -- Build Driver Tables
    --==========================================
    ---------------------------------
    -- DETAIL RECORD - U_EDOENYLBAX_drvTbl
    ---------------------------------
    IF OBJECT_ID('U_EDOENYLBAX_drvTbl','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_drvTbl;
    SELECT DISTINCT
         drvEEID = xEEID
        ,drvCoID = xCoID
        ,drvDepRecID = CONVERT(varchar(12),'1') --DELETE IF NOT USING DEPENDENT DATA
        ,drvInitialsort = ''
        ,drvSubSort = ''
        ,drvSubSort2 = ''
        -- standard fields above and additional driver fields below
        ,drvEmployeeSSN = eepSSN
        ,drvEmployeeIdentificationNumber = EecEmpNo
        ,drvEmployeeLastName = EepNameLast
        ,drvEmployeeFirstName = EepNameFirst
        ,drvEmployeeMiddleName = EepNameMiddle
        ,drvEmployeeDateofBirth = EepDateOfBirth
        ,drvEmployeeGender = EepGender
        ,drvEmployeeMaritalStatus = eepMaritalStatus
        ,drvEmployeeMailAddress1 = EepAddressLine1
        ,drvEmployeeMailAddress2 = EepAddressLine2
        ,drvEmployeeMailCity = EepAddressCity
        ,drvEmployeeMailState = EepAddressState
        ,drvEmployeeMailZipCode = EepAddressZipCode --Add Dash if More than 5
        ,drvCountryCode = 'US'
        ,drvTypeofAddress = 'R'
        

        ,drvEmplStatusCode = CASE WHEN EecEmplStatus = 'L'   THEN 'L1' 
            WHEN EecEmplStatus = 'T' and EecTermReason = '202' THEN  'RT'
            WHEN EecEmplStatus = 'T' and EecTermReason = '203' THEN  'DI'
            WHEN EecEmplStatus = 'T' and EecTermReason not  IN( '202' , '203')  THEN  'TE' 
                else  'AC' END
        ,drvEmployeeStatEffDate = CASE WHEN  EecEmplStatus = 'T' THEN  EecDateOfTermination ELSE   EecEmplStatusStartDate  END
        ,drvEmployeeDateofHire = EecDateOfLastHire
        ,drvClientLocationCode = eeclocation
        ,drvNumberofProducts = '04'
        ,drvProductCode = CASE WHEN GLIFE IS NOT NULL THEN GLIFE END 
        ,drvPolicySymbol = CASE WHEN GLIFE IS NOT NULL THEN 'FLX' END 
        ,drvPolicyNumber = CASE WHEN GLIFE IS NOT NULL THEN '969835' END 
        ,drvEmpIssuedAmount = CASE WHEN GLIFE IS NOT NULL THEN dbo.dsi_fnpadzero((ISNULL(GLIFE_AMT,0)),9,0)  END 
        ,drvBasicLifeClassCode = CASE WHEN GLIFE IS NOT NULL THEN 
                                        CASE WHEN EecSalaryOrHourly = 'H' THEN '001'
                                              WHEN EecSalaryOrHourly = 'S' THEN '002'
                                              ELSE '000'
                                        END
                                 END 
        ,drvProductCode1 = CASE WHEN AC IS NOT NULL THEN AC END 
        ,drvPolicySymbol1 = CASE WHEN AC IS NOT NULL THEN 'FLX' END 
        ,drvPolicyNumber1 = CASE WHEN AC IS NOT NULL THEN '969835' END 
        ,drvEmpIssuedAmount1 = CASE WHEN AC IS NOT NULL THEN dbo.dsi_fnpadzero((ISNULL(AC_AMT,0)),9,0)  END 
        ,drvVolLifeClassCode = CASE WHEN AC IS NOT NULL THEN 
                                        CASE WHEN EecSalaryOrHourly = 'H' THEN '001'
                                              WHEN EecSalaryOrHourly = 'S' THEN '002'
                                              ELSE '000'
                                        END
                                 END 
        ,drvProductCode2 = CASE WHEN BAD is not null THEN 'BAD' END
        ,drvPolicySymbol2 = CASE WHEN BAD is not null THEN 'OK' END
        ,drvPolicyNumber2 = CASE WHEN BAD is not null THEN '971274' END
        ,drvEmpIssuedAmount2 = CASE WHEN BAD IS NOT NULL THEN dbo.dsi_fnpadzero((ISNULL(BAD_AMT,0)),9,0)  END 
        ,drvBasicAccClassCode = CASE WHEN BAD IS NOT NULL THEN 
                                        CASE WHEN EecSalaryOrHourly = 'H' THEN '001'
                                              WHEN EecSalaryOrHourly = 'S' THEN '002'
                                              ELSE '000'
                                        END
                                 END 
        ,drvProductCode3 = CASE WHEN ADDCode is not null THEN 'ADD' END 
        ,drvPolicySymbol3 = CASE WHEN ADDCode is not null THEN 'OK' END 
        ,drvPolicyNumber3 = CASE WHEN ADDCode is not null THEN '971274' END 
        ,drvEmpIssuedAmount3 = CASE WHEN ADDCode is not null THEN dbo.dsi_fnpadzero((ISNULL(ADDCode_AMT,0)),9,0)  END 
        ,drvVolAccidentClassCode = CASE WHEN ADDCode is not null THEN 
                            CASE WHEN EecSalaryOrHourly = 'H' THEN '001'
                                              WHEN EecSalaryOrHourly = 'S' THEN '002'
                                              ELSE '000'
                                        END
                         END 
    INTO dbo.U_EDOENYLBAX_drvTbl
    FROM dbo.U_EDOENYLBAX_EEList WITH (NOLOCK)
    JOIN dbo.vw_int_EmpComp WITH (NOLOCK)
        ON EecEEID = xEEID 
        AND EecCoID = xCoID
    JOIN dbo.EmpPers WITH (NOLOCK)
        ON EepEEID = xEEID
    JOIN (Select 
                bdmeeid,
                bdmcoid, 
                GLIFE  = MAX( CASE WHEN bdmdedcode  = 'GLIFE' THEN 'AF' END),
                GLIFE_AMT = MAX(CASE WHEN Bcadedcode = 'GLIFE' THEN bcabenamtcalc END ),
                AC  = MAX( CASE WHEN bdmdedcode IN ('ALFE2', 'ALFE3', 'ALFE4', 'ALFE5' , 'ALFE6') THEN 'AC' END),
                AC_AMT = MAX(CASE WHEN Bcadedcode IN ('ALFE2', 'ALFE3', 'ALFE4', 'ALFE5' , 'ALFE6')  THEN bcabenamtcalc END ),
                BAD  = MAX( CASE WHEN bdmdedcode IN ('AADD') THEN 'BAD' END),
                BAD_AMT = MAX(CASE WHEN Bcadedcode IN ('AADD')  THEN bcabenamtcalc END ),
                ADDCode  = MAX( CASE WHEN bdmdedcode IN ('ADDE1', 'EADD1', 'EADD2', 'EADD3', 'EADD4', 'EADD5') THEN 'AD' END),
                ADDCode_AMT = MAX(CASE WHEN Bcadedcode IN ('ADDE1', 'EADD1', 'EADD2', 'EADD3', 'EADD4', 'EADD5')  THEN bcabenamtcalc END )
            from dbo.U_dsi_BDM_EDOENYLBAX WITH (NOLOCK) LEFT JOIN dbo.U_dsi_bdm_BenCalculationAmounts WITH (NOLOCK)
            on bcaeeid = bdmeeid and bcacoid = bdmcoid and bcadedcode = bdmdedcode
            Where bcaformatcode = 'EDOENYLBAX'
            group by bdmeeid,bdmcoid
        ) BdmConsolidated
        on BdmConsolidated.bdmeeid = xeeid
        and BdmConsolidated.bdmcoid =  xcoid
    ---------------------------------
    -- HEADER RECORD
    ---------------------------------
    IF OBJECT_ID('U_EDOENYLBAX_Header','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_Header;
    SELECT DISTINCT
         drvFileRecordIdentifier = 'HD'
        ,drvDUNSNumber = '052028345B'
        ,drvProductionTestIndicator = Case when @ExportCode = 'TEST' Then 'T' Else 'P' END
        ,drvFileCreationDate = GETDATE()
        ,drvEmployerName = 'THE DOE RUN COMPANY'
        ,drvFileType = '1'
        ,drvLifeAccidentFileMaintReasonType = 'EN'
    INTO dbo.U_EDOENYLBAX_Header
    ;
    ---------------------------------
    -- TRAILER RECORD
    ---------------------------------
    IF OBJECT_ID('U_EDOENYLBAX_Trailer','U') IS NOT NULL
        DROP TABLE dbo.U_EDOENYLBAX_Trailer;
    SELECT DISTINCT
         drvFileRecordIdentifier = 'TR'
        ,drvDUNSNumber = '052028345B'
        ,drvNumberofRecords = (Select count(*) from dbo.U_EDOENYLBAX_drvTbl)
    INTO dbo.U_EDOENYLBAX_Trailer
    ;

    --==========================================
    -- Set FileName
    --==========================================
    IF (dbo.dsi_fnVariable(@FormatCode,'UseFileName') = 'N')
    BEGIN
        UPDATE dbo.U_dsi_Parameters
            SET ExportFile = CASE WHEN dbo.dsi_fnVariable(@FormatCode,'Testing') = 'Y' THEN 'Test_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  WHEN @ExportCode LIKE 'OE%' THEN 'OE_Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                                  ELSE 'Filename_' + CONVERT(VARCHAR(8),GETDATE(),112) + '.txt'
                             END
        WHERE FormatCode = @FormatCode;
    END

END;
